import { Component, OnInit } from '@angular/core';
import { Foods } from '../shared/models/food';
import { ActivatedRoute, Router } from '@angular/router';
import { FoodService } from '../services/food/food.service';
import { CartService } from '../services/cart.service';

@Component({
  selector: 'app-foodpage',
  templateUrl: './foodpage.component.html',
  styleUrls: ['./foodpage.component.css']
})
export class FoodpageComponent implements OnInit{
  food!: Foods;

  constructor(private activatedRoute:ActivatedRoute, private foodService:FoodService, private cartService:CartService, private router:Router) 
  {
    activatedRoute.params.subscribe
    (
      (params)=>
      {
        if(params['id'])
          this.food = foodService.getFoodById(params['id'])
      }
    )
  }

  ngOnInit(): void {

  }

  addToCart()
  {
    this.cartService.addToCart(this.food);
    this.router.navigateByUrl('/cart-page');
  }
}
<div class="container">
    <img [src]="food.imageUrl" [alt]="food.name">

    <div class="details">
        <div class="header">
            <span class="name">
                {{ food.name }}
            </span>

            <span class="favourite {{food.favourite?'' : 'not'}}">
                ♥
            </span>
        </div>

        <div class="origins">
            <span *ngFor="let origins of food.origins">
                {{ origins }}
            </span>
        </div>

        <div class="tag">
            <app-tags [foodPageTags]="food.tags" justifyContent="start"></app-tags>
        </div>

        <div class="cook-time">
            <span>
                Time to cook about <strong>{{food.cookTime}}</strong> mins
            </span>
        </div>

        <div class="price">
            <span>
                <strong>{{food.price| currency}}</strong>
            </span>
        </div>

        <button (click)="addToCart()">Add to Cart</button>
    </div>
</div>
