import { Component, OnInit } from '@angular/core';
import { FoodService } from '../services/food/food.service';
import { Foods } from '../shared/models/food';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  foods:Foods[]= [];

  constructor(private fs:FoodService, private route:ActivatedRoute) { }

  ngOnInit(): void {
    this.route.params.subscribe(params =>{
      if(params['searchItem'])
        this.foods = this.fs.getAll().filter(food => food.name.toLowerCase().includes(params['searchItem'].toLowerCase()));

      else if(params['tag'])
        this.foods = this.fs.getAllFoodByTag(params['tag'])

      else
      this.foods = this.fs.getAll();
    })



  }

}
<app-search></app-search>
<app-tags></app-tags>
<app-not-found [visible]="!foods || foods.length <= 0"></app-not-found>
<ul>
    <li *ngFor="let food of foods">
        <a routerLink="/food/{{food.id}}">
            <img src="{{food.imageUrl}}" alt="food">
            <div class="content">
                <div class="name">{{food.name}}</div>

            <span class="favourite {{food.favourite?'':'not'}}">â™¥</span>
            <div class="product-item-footer">
                <div class="origins">
                    <span *ngFor = 'let origin of food.origins'>
                        {{origin}}
                    </span>
                </div>

                <div class="cook-time">
                    <span>ðŸ•—</span>
                    {{food.cookTime}}
                </div>
            </div>
            <div class="price">
                {{food.price | currency}}
            </div>
        </div>
        </a>
    </li>
</ul>
